CREATE TABLE SAMPLECUSTOMER
(
  COMPANYNAME          VARCHAR2(2000 BYTE),
  FIRSTNAME             VARCHAR2(2000 BYTE),
  CREATEDDATE           VARCHAR2(2000 BYTE),
  CUSTOMERTYPE          VARCHAR2(2000 BYTE),
  CUSTOMERSTREET        VARCHAR2(2000 BYTE),
  CUSTOMERSUITE         VARCHAR2(2000 BYTE),
  CUSTOMERCITY          VARCHAR2(2000 BYTE),
  CUTOMERSTATE          VARCHAR2(2000 BYTE),
  CUSTOMERZIP           VARCHAR2(2000 BYTE),
  CUSTOMERPHONE         VARCHAR2(2000 BYTE),
  CUSTOMERFAX           VARCHAR2(2000 BYTE),
  BILLCUSTOMERSTREET    VARCHAR2(2000 BYTE),
  BILLCUSTOMERSUITE     VARCHAR2(2000 BYTE),
  BILLCUSTOMERCITY      VARCHAR2(2000 BYTE),
  BILLCUSTOMERSTATE     VARCHAR2(2000 BYTE),
  BILLCUSTOMERZIP       VARCHAR2(2000 BYTE),
  CUSTOMERTAXEXEMPT#    VARCHAR2(2000 BYTE),
  CUSTOMERREFERRALTYPE  VARCHAR2(2000 BYTE),
  CONTACTFIRSTNAME      VARCHAR2(2000 BYTE),
  CONTACTLASTNAME       VARCHAR2(2000 BYTE),
  CONTACTDEARNAME       VARCHAR2(2000 BYTE),
  CONTACTTITLE          VARCHAR2(2000 BYTE),
  CONTACTADDRESS        VARCHAR2(2000 BYTE),
  CONTACTSTREET         VARCHAR2(2000 BYTE),
  CONTACTSUITE          VARCHAR2(2000 BYTE),
  CONTACTCITY           VARCHAR2(2000 BYTE),
  CONTACTSTATE          VARCHAR2(2000 BYTE),
  CONTACTZIP            VARCHAR2(2000 BYTE),
  CONTACTPHONE          VARCHAR2(2000 BYTE),
  CONTACTFAX            VARCHAR2(2000 BYTE),
  CONTACTMOBILE         VARCHAR2(2000 BYTE),
  CONTACTEMAIL          VARCHAR2(2000 BYTE),
  BUSINESSUNIT          VARCHAR2(2000 BYTE)
);

REM INSERTING into SAMPLECUSTOMER
SET DEFINE OFF;

Commit;

delete from samplecustomer where companyname is null;
Commit;

declare
lCustomerID number;
lContactID number;
lCusType number;
lAddressId number;
lBillAddressId number;
lWorkAddressId number;
lCBUAssociationsID number;
lreferraltypeID number;
lCTypeID number;
begin
    for data in(select * from samplecustomer)
    loop
                        Id, 
                        Street, 
                        Suite, 
                        City, 
                        State,
                        Zip, 
                        HomePhone, 
                        HomeFax
                    )
                    values
                    (
                        Address_PK_SEQ.nextval, 
                        data.CUSTOMERSTREET,
                        data.CUSTOMERSUITE, 
                        data.CUSTOMERCITY,  
                        (select id from state where lower(code)= lower(data.CUTOMERSTATE)), 
                        trim(data.CUSTOMERZIP), 
                        data.CUSTOMERPHONE, 
                        data.CUSTOMERFAX
                    )Returning Id into lAddressId;
        
        /* Inserting Customer Billing Address into the Address Table */
                        Id,
                        Street,
                        Suite,
                        City,
                        State,
                        Zip
                    )
                    values
                    (
                        Address_PK_SEQ.nextval,
                        data.BILLCUSTOMERSTREET,
                        data.BILLCUSTOMERSUITE, 
                        data.BILLCUSTOMERCITY,
                        (select id from state where lower(code)= lower(data.BILLCUSTOMERSTATE)),
                        trim(data.BILLCUSTOMERZIP)
                    )Returning Id into lBillAddressId;
                    
            Begin
            select id into lreferraltypeID from referraltype where upper(type)=upper(trim(data.CUSTOMERREFERRALTYPE));
            Exception
            When no_data_found then
            end;
            
                
        
        /* Insert Customer Information into the Customer Table */
            Begin
             select id into lCustomerID from customer where upper(companyname)=upper(data.companyname);
            Exception
                when no_data_found then
                        ID,
                        CompanyName,
                        FirstName,
                        Salutation,
                        Address_ID,
                        BillAddress_ID,
                        ReferralType_Id,
                        mailinglist,
                        TaxExempt,
--                      LastUpdated,
                        User_Id,
                        CreatedDate,
                        CreatedBy

                    )
                    values
                    (
                        Customer_PK_SEQ.nextval,
                        data.COMPANYNAME,
                        data.FIRSTNAME,
                        0,
                        lAddressId,
                        lBillAddressId,
                        lreferraltypeID,
                        1,
                        nvl(data.CUSTOMERTAXEXEMPT#,0),
--                      sysdate,
                        -1,   -- updating only at the time of inserting the record, so considering it as created by
                        sysdate,
                        -1 -- have a editable field in the UI earlier before the patch 63, the user can enter any name here, so ignore it 

                    )Returning Id into lCustomerID;
                end;


                        Id,
                        Name,
                        Street,
                        Suite,
                        City,
                        State,
                        Zip,
                        WORKPhone,
                        HomeFax,
                        Cell,
                        Email
                    )
                    values
                    (
                        Address_PK_SEQ.nextval,
                        data.CONTACTADDRESS,
                        data.CONTACTSTREET,
                        data.CONTACTSUITE, 
                        data.CONTACTCITY,
                        (select id from state where lower(code)= lower(data.CONTACTSTATE)),
                        trim(data.CONTACTZIP),
                    substr(data.contactphone,0,20),
                        data.CONTACTFAX,
                        data.CONTACTMOBILE,
                        data.CONTACTEMAIL
                    )Returning Id into lWorkAddressId;
               
                 
                    
        /* Inserting data into the CustomerContact Table */
                        Id,
                        Customer_Id,
                        FirstName,
                        LastName,
                        Address_Id, 
                        DearName, 
                        Title,
                        CreatedBy,
                        CreatedDate
                    )
                    values
                    (
                        CustomerContact_PK_SEQ.nextval,
                        lCustomerID,
                        nvl(data.CONTACTFIRSTNAME,data.companyname),
                        nvl(data.CONTACTLASTNAME,data.companyname),
                        lWorkAddressId,
                        data.CONTACTDEARNAME,
                        data.CONTACTTITLE,
                        -1,
                        SysDate
                    ) Returning Id  into lContactID;
                        
       
        Begin
            select id into lCusType
            from customertype
            where upper(trim(type)) = upper(trim(data.CUSTOMERTYPE));
        Exception
            when no_data_found then
        End;
        begin
        select customer_id  into lCTypeID from customerbuassociations where customer_id=lCustomerID;
        Exception
        When no_data_found then
        end;
        begin
        select BUSINESSUNITS_ID  into lCBUAssociationsID from customerbuassociations where customer_id=lCustomerID;
        Exception
        When no_data_found then
        end;

        
    end loop;
    commit;
end;
/

update address set zip=replace(zip,'.0','') where zip is not null and instr(zip,'.0')>0;