update samplemenumod set 
category = trim(category),
subcategory= trim(subcategory),
KITCHENNAME=trim(KITCHENNAME),
SALESNAME=trim(SALESNAME),
DESCRIPTION=trim(DESCRIPTION),
KITCHENDESCRIPTION=trim(KITCHENDESCRIPTION),
NOTES=trim(NOTES),
Temperature=trim(Temperature),
unit = trim(unit);
Commit;

--Updating existing menu items data, reasssinging categories and subcategories, if they(CAT & SubCat) doesnt exist create new category and sub category
declare
lcategory  number;
lsubcategory number;
lsub  varchar2(2000);
lunit   number;
ltypearr       parse.arr_str;
ltype number;
begin
  
    for data in(select * from samplemenumod)
    loop
    lsub:=data.subcategory;
         begin
            select id into lcategory
            from menucategory
            where upper(trim(name)) = upper(data.category);
        exception
            when no_data_found then
                -- select id into lcategory
            --from menucategory
            --where  upper(trim(name))= upper(data.category);
        end;
       
        begin
            select id into lsubcategory
            from menusubcategory
            where menucategory_id = lcategory
           and upper(trim(subcategory)) = upper(data.subcategory);
        exception
            when no_data_found then
                 --select id into lsubcategory
            --from menusubcategory
           -- where menucategory_id = lcategory 
            --and upper(trim(subcategory))= upper(data.subcategory);
        end;
        if data.unit is not null then
        begin
            select id into lunit
            from unit
            where unittype_id = 1
            and upper(name) = upper(data.unit);
        exception
            when no_data_found then
                  select id into lunit
            from unit
            where unittype_id = 1
            and upper(name) = upper(data.unit);
        end;
        end if;
        update menuitem set 
        menusubcategory_id = lsubcategory,
        kitchenname = nvl(data.kitchenname,data.salesname),
        salesname = data.salesname,
        description = data.description,
        kitchendescription=data.KITCHENDESCRIPTION,
        notes=data.notes,
        temperature=data.temperature,
        unit_id= lunit,
        cost=data.cost,
        minorderqty =data.minorderqty,
        servedqty=data.served,
        price=data.price,
        active=data.active,
        sortorder=data.sortorder
        where id =data.menuitemid;
        
        delete from menutypeassociations where menuitem_id = data.menuitemid;
        
         if data.menutypes is not null then
                    parse.parse_list(data.menutypes,',',ltypearr);
                    if ltypearr.last>0 then
                        for i in ltypearr.first .. ltypearr.last
                        loop
                            if ltypearr(i) is not null then
                                begin
                                    select id 
                                    into ltype
                                    from menutype
                                    where lower(type) = lower(trim(ltypearr(i)));
                                exception
                                    when no_data_found then
                                        select id 
                                        into ltype
                                        from menutype
                                        where lower(type) = lower(trim(ltypearr(i)));
                                end;
                            end if;
                        end loop;
                    end if;
              end if;
    end loop;
    
    commit;
exception
  when others then
    dbms_output.put_line(lsub);
end;
