-- Import Data into table SAMPLEMENUMOD from file C:\Scripts and others\FFt\FFT Menu Items 2016 - working clean up.xlsx . Task successful and sent to worksheet.
commit;

update samplemenumod set category = trim(category),subcategory= trim(subcategory), unit = trim(unit);

update menucategory set name= name|| ' - inactive' where active=0 and length(name) < 40;

update menusubcategory set subcategory= subcategory|| ' - Inactive' where active=0 and length(subcategory) < 40;
declare
lcategory  number;
lsubcategory number;
lsub  varchar2(2000);
lunit   number;
 ltypearr       parse.arr_str;
 ltype number;
begin
  
    for data in(select * from samplemenumod)
    loop
    lsub:=data.subcategory;
         begin
            select id into lcategory
            from menucategory
            where upper(trim(name)) = upper(data.category);
        exception
            when no_data_found then
                insert into menucategory values(menucategory_pk_seq.nextval, 1, data.category,null);
                 select id into lcategory
            from menucategory
            where  upper(trim(name))= upper(data.category);
        end;
       
        begin
            select id into lsubcategory
            from menusubcategory
            where menucategory_id = lcategory
           and upper(trim(subcategory)) = upper(data.subcategory);
        exception
            when no_data_found then
                insert into menusubcategory values(menusubcategory_pk_seq.nextval, 1, lcategory,data.subcategory,null);
                 select id into lsubcategory
            from menusubcategory
            where menucategory_id = lcategory 
            and upper(trim(subcategory))= upper(data.subcategory);
        end;
        if data.unit is not null then
        begin
            select id into lunit
            from unit
            where unittype_id = 1
            and upper(name) = upper(data.unit);
        exception
            when no_data_found then
                insert into unit values(unit_pk_seq.nextval, 1, data.unit,1,null);
                  select id into lunit
            from unit
            where unittype_id = 1
            and upper(name) = upper(data.unit);
        end;
        end if;
        update menuitem set menusubcategory_id = lsubcategory,
        kitchenname = nvl(data.kitchenname,data.salesname),
        salesname = data.salesname,
        description = data.description,
        unit_id= lunit,
        cost=data.cost,
        minorderqty =data.minorderqty,
        servedqty=data.served,
        price=data.price,active=data.active
        where id =data.menuitemid;
        
        delete from menutypeassociations where menuitem_id = data.menuitemid;
        
         if data.menutypes is not null then
                    parse.parse_list(data.menutypes,',',ltypearr);
                    if ltypearr.last>0 then
                        for i in ltypearr.first .. ltypearr.last
                        loop
                            if ltypearr(i) is not null then
                                begin
                                    select id 
                                    into ltype
                                    from menutype
                                    where lower(type) = lower(trim(ltypearr(i)));
                                exception
                                    when no_data_found then
                                        insert into menutype values(menutype_pk_seq.nextval,1,trim(ltypearr(i)),null);
                                        select id 
                                        into ltype
                                        from menutype
                                        where lower(type) = lower(trim(ltypearr(i)));
                                end;
                            end if;
                            insert into menutypeassociations values(data.menuitemid,ltype);
                        end loop;
                    end if;
              end if;
    end loop;
    
    commit;
exception
  when others then
    dbms_output.put_line(lsub);
end;
